import React, { useState } from 'react';
import '../styles/HamburgerMenu.css';

// Importar os ícones SVG
import LinkedInIcon from '../images/linkedin.svg';
import GitHubIcon from '../images/github.svg';
import InstagramIcon from '../images/instagram.svg';

const HamburgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
    document.body.classList.toggle('no-scroll', isOpen); // Adiciona/Remove a classe 'no-scroll'
  };

  return (
    <div>
      <div className={`hamburger ${isOpen ? 'active' : ''}`} onClick={toggleMenu}>
        <span className="bar"></span>
        <span className="bar"></span>
        <span className="bar"></span>
      </div>
      <div className={`nav-content ${isOpen ? 'active' : ''}`}>
        <ul>
          <li><a href="#home" onClick={toggleMenu}>Home</a></li>
          <li><a href="#projects" onClick={toggleMenu}>Projects</a></li>
          <li><a href="#competence" onClick={toggleMenu}>Competence</a></li>
          <li><a href="#about" onClick={toggleMenu}>About</a></li>
        </ul>
        <div className="social-links">
          <ul>
            <li>
              <a href="https://www.linkedin.com/in/erikasyamamoto/" target="_blank" rel="noopener noreferrer" onClick={toggleMenu}>
                <img src={LinkedInIcon} alt="LinkedIn" />
              </a>
            </li>
            <li>
              <a href="https://github.com/esyamamoto" target="_blank" rel="noopener noreferrer" onClick={toggleMenu}>
                <img src={GitHubIcon} alt="GitHub" />
              </a>
            </li>
            <li>
              <a href="https://www.instagram.com/erika_sayurii/?hl=en" target="_blank" rel="noopener noreferrer" onClick={toggleMenu}>
                <img src={InstagramIcon} alt="Instagram" />
              </a>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default HamburgerMenu;
