import React from 'react';
import '../styles/Competence.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faJava, faReact, faNodeJs } from '@fortawesome/free-brands-svg-icons'; // Corrija a importação


const competenceData = [
  {
    id: 1,
    title: 'Front-End',
    description: 
    'Requisições a APIs |  React com Typescript | JavaScript, HTML & CSS | Testes automatizados com React Testing Library | CSS Modules | Gerenciamento de estado com Redux | Context API | Postman | Cypress | Bootstrap', 
    icon: <FontAwesomeIcon icon="fa-brands fa-react" style={{color: "#fafafa",}} />,
  },
  {
    id: 2,
    title: 'Back-End',
    description: 
    'Java | Spring | Python |   Node.js | Express.JS |  ORM |  Docker |  Testes Unitários |  Testes de Integração |  Banco de dados - SQL : MySQL e PostgreSQL | ORM : Sequelize | Deploy | Postman | Cypress',

  },
  {
    id: 3,
    title: 'Comunicação e trabalho em equipe',
    description: 
    'Git & Github |  Miro | Trello (Kanban + Scrum)',

  },
];

const Competence = () => {
  return (
    <section id="competence">
      <header>
        <h2>Habilidades</h2>
      </header>
      <div className="cards">
        {competenceData.map((competence) => (
          <div key={competence.id} className="card">
            <div className="icon">{competence.icon}</div>
            <h3>{competence.title}</h3>
            <p>{competence.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Competence;
