import React, { useState } from "react";

const CardCarousel = () => {
  const [activeCard, setActiveCard] = useState(0);

  const cards = [
    {
      title: "Card 1",
      content: "This is the content of the first card.",
    },
    {
      title: "Card 2",
      content: "This is the content of the second card.",
    },
    {
      title: "Card 3",
      content: "This is the content of the third card.",
    },
  ];

  const handleNext = () => {
    setActiveCard((activeCard + 1) % cards.length);
  };

  const handlePrevious = () => {
    setActiveCard((activeCard - 1 + cards.length) % cards.length);
  };

  return (
    <div className="carousel">
      <button onClick={handlePrevious} disabled={activeCard === 0}>
        Previous
      </button>
      <div className="card-container">
        <div className="card">
          <h2>{cards[activeCard].title}</h2>
          <p>{cards[activeCard].content}</p>
        </div>
      </div>
      <button onClick={handleNext} disabled={activeCard === cards.length - 1}>
        Next
      </button>
    </div>
  );
};

export default CardCarousel;
