import React from 'react';
import '../styles/Competence.css';

const competenceData = {
  frontEnd: [
    {
      title: 'JavaScript',
      description: 'A maior parte da minha experiência com JavaScript foi através do curso da Trybe, onde lidei com diversos desafios e concluí projetos complexos, alcançando um nível intermediário na linguagem.',
      icon: '/path-to-your-icons/logo-javascript.svg'
    },
    {
      title: 'HTML',
      description: 'Atualmente, considero meu nível em HTML avançado. Adquiri experiência principalmente através de estudos autodidatas e desenvolvimento de aplicações complexas.',
      icon: '/path-to-your-icons/logo-html5.svg'
    },
    {
      title: 'CSS',
      description: 'Assim como o HTML, meu nível em CSS é avançado, desenvolvido através de estudos dedicados e aplicação prática em projetos diversos.',
      icon: '/path-to-your-icons/logo-css3.svg'
    },
    {
      title: 'ReactJS',
      description: 'Iniciei meus estudos em ReactJS buscando eficiência no desenvolvimento de aplicações web, obtendo habilidades significativas em comparação ao JavaScript puro.',
      icon: '/path-to-your-icons/logo-react.svg'
    },
    {
      title: 'React com TypeScript',
      description: 'Utilizo TypeScript para desenvolver aplicações mais robustas e com menos erros, integrando-o com React para melhorar a qualidade e manutenibilidade do código.',
      icon: '/path-to-your-icons/logo-typescript.svg'
    },
    {
      title: 'Bootstrap',
      description: 'Domino o Bootstrap para criar interfaces responsivas e estilizadas de forma eficiente em projetos web.',
      icon: '/path-to-your-icons/logo-bootstrap.svg'
    },
    {
      title: 'Redux',
      description: 'Utilizo Redux para gerenciar estados complexos em aplicações React, melhorando a escalabilidade e a organização do código.',
      icon: '/path-to-your-icons/logo-redux.svg'
    }
  ],
  backEnd: [
    {
      title: 'Node.js',
      description: 'Desenvolvo aplicações backend eficientes com Node.js e Express.js, integrando APIs e gerenciando requisições de forma otimizada.',
      icon: '/path-to-your-icons/logo-nodejs.svg'
    },
    {
      title: 'Java',
      description: 'Experiência em desenvolvimento backend utilizando Java e Spring para aplicações corporativas robustas e escaláveis.',
      icon: '/path-to-your-icons/logo-java.svg'
    },
    {
      title: 'Python',
      description: 'Conhecimento em Python para desenvolvimento web e automação, aplicando em projetos que exigem eficiência e simplicidade.',
      icon: '/path-to-your-icons/logo-python.svg'
    },
    {
      title: 'Docker',
      description: 'Utilizo Docker para empacotar e distribuir aplicações, facilitando o desenvolvimento e a implementação em ambientes diversos.',
      icon: '/path-to-your-icons/logo-docker.svg'
    },
    {
      title: 'Banco de dados - SQL',
      description: 'Experiência com bancos de dados SQL como MySQL e PostgreSQL, aplicando ORMs como Sequelize para facilitar a integração e manipulação de dados.',
      icon: '/path-to-your-icons/logo-mysql.svg'
    },
    {
      title: 'Deploy',
      description: 'Experiência em deploy de aplicações utilizando ferramentas modernas para garantir eficiência e segurança.',
      icon: '/path-to-your-icons/cloud-outline.svg'
    }
  ],
  communication: [
    {
      title: 'Git & Github',
      description: 'Utilizo Git e Github para controle de versão e colaboração eficiente em projetos de desenvolvimento.',
      icon: '/path-to-your-icons/logo-github.svg'
    },
    {
      title: 'Trello (Kanban + Scrum)',
      description: 'Uso Trello para gerenciar projetos utilizando metodologias ágeis como Kanban e Scrum, facilitando a organização e o acompanhamento das tarefas.',
      icon: '/path-to-your-icons/logo-trello.svg'
    },
    {
      title: 'Postman',
      description: 'Utilizo o Postman para testar APIs e automatizar testes de integração, garantindo a qualidade e confiabilidade das aplicações.',
      icon: '/path-to-your-icons/logo-postman.svg'
    },
    {
      title: 'Cypress',
      description: 'Utilizo Cypress para testes automatizados de ponta a ponta, garantindo que as aplicações funcionem conforme o esperado em diferentes cenários.',
      icon: '/path-to-your-icons/logo-cypress.svg'
    },
    {
      title: 'Miro',
      description: 'Uso Miro para colaboração e brainstorming em equipe, facilitando a visualização e organização de ideias em projetos complexos.',
      icon: '/path-to-your-icons/logo-miro.svg'
    }
  ]
};

const Competence = () => {
  return (
    <section id="competence">
      <header>
        <h2>My Competences</h2>
      </header>
      <div className="cards">
        {competenceData.map((competence) => (
          <div key={competence.id} className="card">
            <div className="icon">{competence.icon}</div>
            <h3>{competence.title}</h3>
            <p>{competence.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Competence;
